sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values older
// than the current time.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var NotoverdueNITasks = new GlideRecord('x_58872_needit_needit_task');
NotoverdueNITasks.addQuery('due_date', '>=', rightNow);
NotoverdueNITasks.addQuery('due_date', '<', gs.hoursAgo(-24));
NotoverdueNITasks.addQuery('state', '!=', 3);

NotoverdueNITasks.query();
// Write a log message for each overdue NeedIt Task Record
while (NotoverdueNITasks.next()) {
    gs.info("ME: Notoverdue NeedIt Task record = " + NotoverdueNITasks.number);
    gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', NotoverdueNITasks, NotoverdueNITasks.number, gs.getUserID());
}
